import React, { useContext, useState } from "react";
import { Container, Form, Button } from "react-bootstrap";
import { AuthUserCtx } from "../../context/authUser";
import { useFormik } from "formik";
import axios from "axios";

const defaultImg =
  "data:image/png;base64,";

export const Me = () => {
  const { authUser } = useContext(AuthUserCtx);

  const { photoUrl, setPhotoUrl } = useState(authUser.photoUrl);

  const formik = useFormik({
    initialValues: {
      displayName: authUser.displayName,
      address: authUser.address,
    },
    onSubmit: (value) => {
      const jwt = localStorage.getItem("jwt");
      axios.put("http://localhost:5000/auth/me", value, {
        headers: {
          Authorization: "Bearer " + jwt,
        },
      });
    },
  });

  const { handleSubmit, handleChange, values } = formik;

  return (
    <Container className="mt-5">
      <h1>Configuration for {authUser.username}</h1>
      <div className="d-flex align-items-center flex-column my-3">
        <img
          src={photoUrl || defaultImg}
          alt="Image User"
          className="border rounded-circle"
          style={{ width: "150" }}
        />
        <Form.File
          label="Select image your device "
          custom
          style={{ maxWidth: 400}}
          className="mt-2"
          onChange = {
              (e) =>{
                  setPhotoUrl(e.target.files[0])
              }
          }
        />
      </div>
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="formDisplayName">
          <Form.Label>Display Name</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter Display Name"
            name="displayName"
            value={values.displayName}
            onChange={handleChange}
          />
        </Form.Group>

        <Form.Group controlId="formDisplayName">
          <Form.Label>Address</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter address"
            name="address"
            value={values.address}
            onChange={handleChange}
          />
        </Form.Group>

        <Button variant="primary" type="submit">
          Update
        </Button>
      </Form>
    </Container>
  );
};
