import React from "react";
import { Card, Button ,Form } from "react-bootstrap";
import style from "./composer.module.css";

const images = [
  "https://www.freecodecamp.org/news/content/images/2020/04/w-qjCHPZbeXCQ-unsplash.jpg",
  "https://cdn.wallpapersafari.com/21/24/pELVjk.jpg",
];

export const Composer = () => {
  return (
    <Card>
      <Card.Body>
        <Card.Title>What are you thinking?</Card.Title>
        <Form.Control as="textarea" row="3" className={style.composer}></Form.Control>
        <div className="d-flex justify-content-between mt-2">
          <div>{images.map(item => <img src={item} key={item} alt="" className="border rounded-circle mr-1" style={{width : 30 , height:30}} />)}</div>
          <Button variant="primary">Post</Button>
        </div>
      </Card.Body>
    </Card>
  );
};
