import React , {useContext} from 'react'
import { Navbar , Nav , Container ,OverlayTrigger , Popover , ListGroup} from "react-bootstrap";
import { Link , Route , Switch } from "react-router-dom";
import {useAuth} from '../../../hooks/useAuth';
import { Redirect } from 'react-router-dom';
import {AuthUserCtx}  from "../../../context/authUser";
import { NewsFeed } from '../NewsFeed/NewsFeed';

export const Home = () => {
    const fetchMeApiData = useAuth();
    const {authUser}  = useContext(AuthUserCtx);

    if (fetchMeApiData.loading)
        return <div>Authenticating ....</div>;

    if (fetchMeApiData.error)
        return <Redirect to="/auth/login" />;

    return (
        <div>
        <Navbar bg="light" expand="lg">
        <Container>
          <Navbar.Brand>The Social</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav" >
            <Nav className="mr-auto">
              <Nav.Link as={Link} to="/auth/login">
                Search
              </Nav.Link>
              <Nav.Link as={Link} to="/auth/register">
                Message
              </Nav.Link>
            </Nav>
            <OverlayTrigger 
              trigger="click"
              placement = "bottom"
              overlay={
                <Popover> 
                  <Popover.Content>
                    <ListGroup>
                      <ListGroup.Item>
                        <Link to="/me">Update Profile</Link>
                      </ListGroup.Item>
                      <ListGroup.Item>Log Out</ListGroup.Item>
                    </ListGroup>
                    
                  </Popover.Content>
                </Popover>
              }
              >
            {authUser && authUser.username}
            </OverlayTrigger>

          </Navbar.Collapse>
        </Container>
      </Navbar>
      <Switch>
            <Route path="/profile/:username" />
            <Route path="/search" />
            <Route path="/message" />
            <Route path="/" component ={NewsFeed} />
      </Switch>
      </div>
    )
}
