import React from "react";

import { Card } from "react-bootstrap";
import dayjs from "dayjs";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faThumbsUp, faComment } from "@fortawesome/free-regular-svg-icons";
import styles from "./Post.module.css";

export const Post = ({ post , onLikeClick }) => {
  return (
    <Card className="mb-3">
      <Card.Header className="d-flex">
        <img
          src={post.author.photoUrl}
          style={{ width: "50px", height: "50px" }}
          className="border rounded-circle mr-2"
        />
        <div>
          <h6>{post.author.displayName || post.author.username}</h6>
          <small>{dayjs(post.createdAt).format("DD-MM-YYYY HH:MM")}</small>
        </div>
      </Card.Header>
      <Card.Body
        className="p-4 text-left"
        style={{
          backgroundImage: `url(${post.image})`,
          fontSize: "36px",
          textShadow: "2px 2px 4px #ffffff",
        }}
      >
        {post.content}
      </Card.Body>
      <div className="d-flex p-2">
        <div className={styles.iconButton} onClick={onLikeClick} >
          <FontAwesomeIcon icon={faThumbsUp} />
          <span>{post.likes ? console.log(post.likes[0]) : 0}</span>
        </div>
        <div className={styles.iconButton}>
          <FontAwesomeIcon icon={faComment} />
          <span>10</span>
        </div>
      </div>
    </Card>
  );
};
