import React, { useContext } from "react";
import { Container, Form, Button } from "react-bootstrap";
import { AuthUserCtx } from "../../context/authUser";
import {useFormik} from "formik";
import axios from 'axios';

export const Me = () => {
  const { authUser } = useContext(AuthUserCtx);

  const formik = useFormik({
    initialValues: {
      displayName: authUser.displayName,
      address : authUser.address
    },
    onSubmit : (value) =>{
        const jwt = localStorage.getItem('jwt');
        axios.put('http://localhost:5000/auth/me' , value , {
            headers : {
                Authorization :  "Bearer " + jwt
            }
        })
    }
  });

  const {handleSubmit , handleChange , values} = formik;

  return (
    <Container className="mt-5">
      <h1>Configuration for {authUser.username}</h1>
      <div>
          <img src="" alt ="Image User" className="border rounded-circle" /> 
      </div>
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="formDisplayName">
          <Form.Label>Display Name</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter Display Name"
            name="displayName"
            value={values.displayName}
            onChange={handleChange}
          />
        </Form.Group>

        <Form.Group controlId="formDisplayName">
          <Form.Label>Address</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter address"
            name="address"
            value={values.address}
            onChange={handleChange}
          />
        </Form.Group>

        <Button variant="primary" type="submit">
          Update
        </Button>
      </Form>
    </Container>
  );
};
